<?php

$dsn = 'mysql:host=localhost;dbname=php1';
$dbh = new PDO($dsn, 'root', '');

$sql ='CREATE TABLE `php1`.`news` ( 
`id` SERIAL NOT NULL AUTO_INCREMENT ,
 `newsTitle` VARCHAR(255) NOT NULL , 
 `newsText` TEXT NOT NULL , 
 `newsAuthor` VARCHAR(100) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB';

$sth = $dbh->prepare($sql);
$res = $sth->execute();

if ($res == true) {
    echo 'База успешно построена';
} else {
    echo 'База уже существует';
}

